import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { useAuth } from '../hooks/useAuth';
import './BookingForm.css';

const BookingForm = ({ flightData, onClose }) => {
  const navigate = useNavigate();
  const user = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [bookingData, setBookingData] = useState({
    seat_number: '',
    passenger_name: '',
    passenger_email: '',
    passenger_phone: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // 1. Insert flight data into flights table
      const { data: flightRecord, error: flightError } = await supabase
        .from('flights')
        .insert([
          {
            flight_number: flightData.flightNumber,
            airline_name: flightData.airlineName,
            origin: flightData.origin,
            destination: flightData.destination,
            departure_time: flightData.departureTime,
            arrival_time: flightData.arrivalTime,
            price: flightData.price,
            created_at: new Date().toISOString()
          }
        ])
        .select()
        .single();

      if (flightError) throw flightError;

      // 2. Create booking record
      const { data: bookingRecord, error: bookingError } = await supabase
        .from('bookings')
        .insert([
          {
            user_id: user.id,
            flight_id: flightRecord.id,
            seat_number: bookingData.seat_number,
            passenger_name: bookingData.passenger_name,
            passenger_email: bookingData.passenger_email,
            passenger_phone: bookingData.passenger_phone,
            booking_date: new Date().toISOString(),
            status: 'confirmed',
            created_at: new Date().toISOString()
          }
        ])
        .select()
        .single();

      if (bookingError) throw bookingError;

      // 3. Update user's booking history
      const { error: historyError } = await supabase
        .from('booking_history')
        .insert([
          {
            user_id: user.id,
            booking_id: bookingRecord.id,
            action: 'created',
            created_at: new Date().toISOString()
          }
        ]);

      if (historyError) throw historyError;

      // Success - redirect to confirmation page
      navigate(`/booking-confirmation/${bookingRecord.id}`);
    } catch (error) {
      console.error('Booking error:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <h2>Book Your Flight</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        <div className="flight-summary">
          <h3>Flight Details</h3>
          <p>Flight: {flightData.flightNumber}</p>
          <p>Airline: {flightData.airlineName}</p>
          <p>From: {flightData.origin} to {flightData.destination}</p>
          <p>Departure: {new Date(flightData.departureTime).toLocaleString()}</p>
          <p>Arrival: {new Date(flightData.arrivalTime).toLocaleString()}</p>
          <p>Price: ${flightData.price}</p>
        </div>

        <div className="form-group">
          <label htmlFor="seat_number">Seat Number:</label>
          <input
            type="text"
            id="seat_number"
            name="seat_number"
            value={bookingData.seat_number}
            onChange={handleInputChange}
            required
            placeholder="e.g., 12A"
          />
        </div>

        <div className="form-group">
          <label htmlFor="passenger_name">Passenger Name:</label>
          <input
            type="text"
            id="passenger_name"
            name="passenger_name"
            value={bookingData.passenger_name}
            onChange={handleInputChange}
            required
            placeholder="Full Name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="passenger_email">Email:</label>
          <input
            type="email"
            id="passenger_email"
            name="passenger_email"
            value={bookingData.passenger_email}
            onChange={handleInputChange}
            required
            placeholder="email@example.com"
          />
        </div>

        <div className="form-group">
          <label htmlFor="passenger_phone">Phone:</label>
          <input
            type="tel"
            id="passenger_phone"
            name="passenger_phone"
            value={bookingData.passenger_phone}
            onChange={handleInputChange}
            required
            placeholder="+1234567890"
          />
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="form-actions">
          <button type="button" onClick={onClose} className="cancel-button">
            Cancel
          </button>
          <button type="submit" disabled={loading} className="submit-button">
            {loading ? 'Processing...' : 'Confirm Booking'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default BookingForm;
