import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './AuthPage.css';

const AuthPage = () => {
  const [view, setView] = useState('sign-in'); // 'sign-in' | 'sign-up'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Email/Password Sign Up
  const handleSignUp = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: { data: { full_name: fullName } }
    });
    if (error) setMessage(error.message);
    else setMessage('Check your email for confirmation!');
    setLoading(false);

    // Insert into profiles table
    if (!error && data.user) {
      await supabase.from('profiles').upsert([
        { id: data.user.id, full_name: fullName }
      ]);
    }
  };

  // Email/Password Sign In
  const handleSignIn = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (error) setMessage(error.message);
    else setMessage('Signed in!');
    setLoading(false);
  };

  return (
    <div className="auth-page">
      <h2>{view === 'sign-in' ? 'Sign In' : 'Sign Up'}</h2>
      {view === 'sign-in' && (
        <form onSubmit={handleSignIn}>
          <input type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} required />
          <button type="submit" disabled={loading}>Sign In</button>
          <p>Don't have an account? <span style={{color:'blue',cursor:'pointer'}} onClick={()=>setView('sign-up')}>Sign Up</span></p>
        </form>
      )}
      {view === 'sign-up' && (
        <form onSubmit={handleSignUp}>
          <input type="text" placeholder="Full Name" value={fullName} onChange={e=>setFullName(e.target.value)} required />
          <input type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} required />
          <button type="submit" disabled={loading}>Sign Up</button>
          <p>Already have an account? <span style={{color:'blue',cursor:'pointer'}} onClick={()=>setView('sign-in')}>Sign In</span></p>
        </form>
      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default AuthPage;
