import React, { useState } from 'react';
import amadeusService from '../services/amadeusService';
import '../styles/FlightSearch.css';

const FlightSearch = () => {
  const [searchParams, setSearchParams] = useState({
    originLocationCode: '',
    destinationLocationCode: '',
    departureDate: '',
    returnDate: '',
    adults: 1
  });
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Common airport codes for reference
  const commonAirports = {
    'JFK': 'New York (JFK)',
    'LAX': 'Los Angeles (LAX)',
    'SFO': 'San Francisco (SFO)',
    'ORD': 'Chicago (ORD)',
    'MIA': 'Miami (MIA)',
    'LHR': 'London (LHR)',
    'CDG': 'Paris (CDG)',
    'FRA': 'Frankfurt (FRA)',
    'DXB': 'Dubai (DXB)',
    'SIN': 'Singapore (SIN)'
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({
      ...prev,
      [name]: value.toUpperCase() // Convert airport codes to uppercase
    }));
  };

  const validateAirportCode = (code) => {
    return code.length === 3 && /^[A-Z]{3}$/.test(code);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Validate airport codes
    if (!validateAirportCode(searchParams.originLocationCode)) {
      setError('Please enter a valid 3-letter airport code for origin (e.g., JFK, LAX)');
      setLoading(false);
      return;
    }

    if (!validateAirportCode(searchParams.destinationLocationCode)) {
      setError('Please enter a valid 3-letter airport code for destination (e.g., JFK, LAX)');
      setLoading(false);
      return;
    }

    try {
      const results = await amadeusService.searchFlights(
        searchParams.originLocationCode,
        searchParams.destinationLocationCode,
        searchParams.departureDate,
        searchParams.returnDate,
        searchParams.adults
      );
      setFlights(results.data || []);
    } catch (err) {
      setError(err.message || 'Error searching for flights. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flight-search-container">
      <h2>Search Flights</h2>
      <div className="airport-codes-help">
        <h3>Common Airport Codes:</h3>
        <div className="airport-codes-grid">
          {Object.entries(commonAirports).map(([code, name]) => (
            <div key={code} className="airport-code-item">
              <strong>{code}</strong>: {name}
            </div>
          ))}
        </div>
      </div>
      <form onSubmit={handleSearch} className="search-form">
        <div className="form-group">
          <label>From (Airport Code):</label>
          <input
            type="text"
            name="originLocationCode"
            value={searchParams.originLocationCode}
            onChange={handleInputChange}
            placeholder="e.g., JFK"
            maxLength="3"
            required
          />
        </div>

        <div className="form-group">
          <label>To (Airport Code):</label>
          <input
            type="text"
            name="destinationLocationCode"
            value={searchParams.destinationLocationCode}
            onChange={handleInputChange}
            placeholder="e.g., LAX"
            maxLength="3"
            required
          />
        </div>

        <div className="form-group">
          <label>Departure Date:</label>
          <input
            type="date"
            name="departureDate"
            value={searchParams.departureDate}
            onChange={handleInputChange}
            min={new Date().toISOString().split('T')[0]}
            required
          />
        </div>

        <div className="form-group">
          <label>Return Date:</label>
          <input
            type="date"
            name="returnDate"
            value={searchParams.returnDate}
            onChange={handleInputChange}
            min={searchParams.departureDate || new Date().toISOString().split('T')[0]}
            required
          />
        </div>

        <div className="form-group">
          <label>Number of Adults:</label>
          <input
            type="number"
            name="adults"
            value={searchParams.adults}
            onChange={handleInputChange}
            min="1"
            max="9"
            required
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search Flights'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {flights.length > 0 && (
        <div className="flight-results">
          <h3>Available Flights</h3>
          {flights.map((flight, index) => (
            <div key={index} className="flight-card">
              <div className="flight-info">
                <div className="route">
                  <span>{flight.itineraries[0].segments[0].departure.iataCode}</span>
                  <span>→</span>
                  <span>{flight.itineraries[0].segments[0].arrival.iataCode}</span>
                </div>
                <div className="time">
                  <span>{new Date(flight.itineraries[0].segments[0].departure.at).toLocaleString()}</span>
                  <span>-</span>
                  <span>{new Date(flight.itineraries[0].segments[0].arrival.at).toLocaleString()}</span>
                </div>
                <div className="price">
                  {flight.price.currency} {flight.price.total}
                </div>
              </div>
              <button 
                className="book-button"
                onClick={() => window.open(flight.bookingLink, '_blank')}
              >
                Book Now
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FlightSearch;
