import React, { useState } from 'react';
import { useBooking } from '../context/BookingContext';
import { createBooking } from '../services/api';
import { useNavigate } from 'react-router-dom';

const BookingForm = () => {
  const { selectedFlight, setBookingData } = useBooking();
  const [formData, setFormData] = useState({
    passenger_name: '',
    email: '',
    phone: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showThankYou, setShowThankYou] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedFlight) {
      setError('Please select a flight first');
      return;
    }

    setLoading(true);
    setError(null);
    setShowThankYou(false);

    try {
      const bookingData = {
        ...formData,
        flight_id: selectedFlight.id,
        total_price: selectedFlight.price
      };
      console.log('Submitting booking:', bookingData);
      const data = await createBooking(bookingData);
      console.log('Booking created:', data);

      setBookingData(data);
      setShowThankYou(true);

      setTimeout(() => {
        setShowThankYou(false);
        setBookingData(null);
        setFormData({
          passenger_name: '',
          email: '',
          phone: ''
        });
        navigate('/');
      }, 3000);

    } catch (err) {
      console.error('Booking error:', err);
      setError(err.details || err.message || 'Failed to create booking. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!selectedFlight) {
    return null;
  }

  if (showThankYou) {
    return (
      <div className="thank-you-message" style={{ textAlign: 'center', padding: '20px', backgroundColor: '#e8f5e9', color: '#2e7d32', borderRadius: '8px', marginTop: '20px' }}>
        <h2>Thanks for booking with us!</h2>
        <p>Redirecting you to the home page...</p>
      </div>
    );
  }

  return (
    <div className="booking-form">
      <h2>Book Flight</h2>
      <p>Selected Flight: {selectedFlight.flight_number}</p>
      <p>Price: ${selectedFlight.price}</p>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Passenger Name"
            value={formData.passenger_name}
            onChange={(e) => setFormData({ ...formData, passenger_name: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="tel"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Confirm Booking'}
        </button>
      </form>

      {error && (
        <div style={{ color: 'red', marginTop: '10px', padding: '10px', backgroundColor: '#ffebee', borderRadius: '4px' }}>
          {error}
        </div>
      )}
    </div>
  );
};

export default BookingForm;
