import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './BookingSuccessMessage.css';

const BookingSuccessMessage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/home', { replace: true }); // Redirect after 3 seconds
    }, 3000); // 3000 milliseconds = 3 seconds

    return () => clearTimeout(timer); // Clear the timer if the component unmounts
  }, [navigate]);

  return (
    <div className="booking-success-container">
      <div className="booking-success-card">
        <h2>Thank You for Booking!</h2>
        <p>Your flight booking has been confirmed.</p>
        <p>Redirecting to home page in 3 seconds...</p>
        {/* Optional: Add a loading spinner or animation */}
      </div>
    </div>
  );
};

export default BookingSuccessMessage;
