import React, { useState } from 'react';
import { useBooking } from '../context/BookingContext';
import { searchFlights } from '../services/api';

const FlightSearch = () => {
  const { setSelectedFlight } = useBooking();
  const [searchParams, setSearchParams] = useState({
    origin: '',
    destination: '',
    date: ''
  });
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const data = await searchFlights(searchParams);
      setFlights(data);
    } catch (err) {
      setError('Failed to search flights. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <form className="search-form" onSubmit={handleSearch}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Origin"
            value={searchParams.origin}
            onChange={(e) => setSearchParams({ ...searchParams, origin: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            placeholder="Destination"
            value={searchParams.destination}
            onChange={(e) => setSearchParams({ ...searchParams, destination: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="date"
            value={searchParams.date}
            onChange={(e) => setSearchParams({ ...searchParams, date: e.target.value })}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search Flights'}
        </button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div className="flight-results">
        {flights.map((flight) => (
          <div key={flight.id} className="flight-card">
            <h3>Flight {flight.flight_number}</h3>
            <p>From: {flight.origin}</p>
            <p>To: {flight.destination}</p>
            <p>Date: {new Date(flight.departure_time).toLocaleDateString()}</p>
            <p>Time: {new Date(flight.departure_time).toLocaleTimeString()}</p>
            <p>Price: ${flight.price}</p>
            <button onClick={() => setSelectedFlight(flight)}>
              Select Flight
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FlightSearch;
