import axios from 'axios';

const AMADEUS_API_URL = 'https://test.api.amadeus.com/v2';

class AmadeusService {
  constructor() {
    this.accessToken = null;
    this.tokenExpiration = null;
  }

  async getAccessToken() {
    if (this.accessToken && this.tokenExpiration > Date.now()) {
      return this.accessToken;
    }

    // Debug logging
    console.log('API Key:', process.env.REACT_APP_AMADEUS_API_KEY ? 'Present' : 'Missing');
    console.log('API Secret:', process.env.REACT_APP_AMADEUS_API_SECRET ? 'Present' : 'Missing');

    try {
      const response = await axios.post('https://test.api.amadeus.com/v1/security/oauth2/token', 
        new URLSearchParams({
          'grant_type': 'client_credentials',
          'client_id': process.env.REACT_APP_AMADEUS_API_KEY,
          'client_secret': process.env.REACT_APP_AMADEUS_API_SECRET
        }), {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          }
        }
      );

      this.accessToken = response.data.access_token;
      this.tokenExpiration = Date.now() + (response.data.expires_in * 1000);
      return this.accessToken;
    } catch (error) {
      console.error('Error getting Amadeus access token:', error);
      throw error;
    }
  }

  async searchFlights(originLocationCode, destinationLocationCode, departureDate, returnDate, adults = 1) {
    try {
      const token = await this.getAccessToken();
      const response = await axios.get(`${AMADEUS_API_URL}/shopping/flight-offers`, {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: {
          originLocationCode,
          destinationLocationCode,
          departureDate,
          returnDate,
          adults,
          currencyCode: 'USD',
          max: 10
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error searching flights:', error);
      throw error;
    }
  }

  async getFlightOffersPrice(offer) {
    try {
      const token = await this.getAccessToken();
      const response = await axios.post(
        `${AMADEUS_API_URL}/shopping/flight-offers/pricing`,
        { data: { type: 'flight-offers-pricing', flightOffers: [offer] } },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );
      return response.data;
    } catch (error) {
      console.error('Error getting flight price:', error);
      throw error;
    }
  }
}

export default new AmadeusService();
