import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import './HomePage.css';

const HomePage = () => {
  const user = useAuth();
  const navigate = useNavigate();

  const handleBookFlight = () => {
    navigate('/book'); // You can implement this route for booking
  };

  return (
    <div className="homepage-bg fade-in">
      <header className="homepage-header slide-down">
        <div className="homepage-title">
          <span role="img" aria-label="plane" className="plane-emoji">✈️</span>
          <h1>SkyBooker</h1>
        </div>
        <div className="profile-section pop-in">
          <img src={`https://api.dicebear.com/7.x/identicon/svg?seed=${user?.email || 'user'}`} alt="Profile" className="profile-avatar" />
          <span className="profile-email">{user?.email}</span>
        </div>
      </header>
      <main className="homepage-main zoom-in">
        <h2>Welcome, {user?.user_metadata?.full_name || 'Traveler'}!</h2>
        <p className="homepage-desc">Book your next adventure with ease and style.</p>
        <button className="book-btn pulse" onClick={handleBookFlight}>Book a Flight</button>
      </main>
    </div>
  );
};

export default HomePage;
